package chapter02;

public class GreatestNumberAmongThreeExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x,y,z;
		x=500;
		y=300;
		z=600;
		if(x>y & x>z)
			System.out.println(x +" x is greates");
		else if (y>z)
			System.out.println(y +" y is greates");
		else
			System.out.println(z +" z is greates");
		
	}

}
