package chapter_01;

public class AlgebraAgeCal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double fatherAge;
		double daughterAge;
		daughterAge=10;
		System.out.println("Rita current Age ->" + daughterAge);
		fatherAge=daughterAge*5;
		System.out.println("Rita's father age"+fatherAge);
		// After 5 years
		System.out.println("After 5 years what is age of Rita and Rita father");
		fatherAge+=5;
		daughterAge+=5;
		System.out.println("Rita's Age after 5 year ->" + daughterAge);
		System.out.println("Rita's father Age after 5 years ->" + fatherAge);
		
		
	}

}
