package chapter_01;

public class AssignmentOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Assignment Operator
		// +=,-=,*=,%=
		// a+=100 -> a=a+100;
		// a-=200; -> a=a-200;
		// a*=200= -> a=a*200
		// a/=4 -> a=a/4
		// a%=4 -> a=a%4
		// +=
		int cal=100;
		// add 200 into 'cal' variable
		cal+=200;
		System.out.println("Result of calculation is -> "+cal);
		// add 500 into 'cal' variable
		cal=cal+500;
		System.out.println("Result of calculation is -> "+cal);
			
	}

}
