package chapter_01;

public class CompoundInterestExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double compoundInterest=5000;
		double interestRate=2.5;
		double period=5;
		double calCompoundInterest=1 +(compoundInterest/interestRate)*period;
		System.out.println(calCompoundInterest);
		
		
		
	}

}
