package chapter_01;

public class DataTypeExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,c; //variables named a,b and c
		a=100;
		b=200;
		
		//Arithmetic operator
		// +, -, *, /, % (Modulo)
		System.out.println("Addition -> " + (100+200));
		c=a+b;
		System.out.println("Addition by using variable -> " + c);
		System.out.println("Substraction ->" +(300-200));
		System.out.println("Substraction by using variable ->" + (b-a));
		c=b-a;
		System.out.println("Substraction by using variable calculation ->" +c);
		// Multiply operation
		// Division operation
		// Modulo operation
	}

}
